import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/Registro.css';

const Registro = () => {
  const [formData, setFormData] = useState({
    nombre_completo: '',
    correo: '',
    password: '',
    confirmarPassword: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { registro } = useAuth();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Validaciones
    if (!formData.nombre_completo || !formData.correo || !formData.password || !formData.confirmarPassword) {
      setError('Por favor completa todos los campos');
      setLoading(false);
      return;
    }

    if (formData.password !== formData.confirmarPassword) {
      setError('Las contrase√±as no coinciden');
      setLoading(false);
      return;
    }

    if (formData.password.length < 6) {
      setError('La contrase√±a debe tener al menos 6 caracteres');
      setLoading(false);
      return;
    }

    // Intentar registro
    const result = await registro({
      nombre_completo: formData.nombre_completo,
      correo: formData.correo,
      password: formData.password
    });

    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.error);
    }

    setLoading(false);
  };

  return (
    <div className="registro-container">
      <div className="registro-left">
        <div className="registro-brand">
          <div className="brand-logo">
            <span className="logo-icon">üè¶</span>
          </div>
          <h1>Banco de Bogot√°</h1>
          <p className="brand-subtitle">Sistema de Gesti√≥n de Onboarding</p>
        </div>

        <div className="registro-info">
          <h3>¬øPor qu√© registrarte?</h3>
          <ul className="info-list">
            <li>
              <span className="info-icon">üìä</span>
              <span>Accede al dashboard completo</span>
            </li>
            <li>
              <span className="info-icon">üìÖ</span>
              <span>Consulta eventos de onboarding</span>
            </li>
            <li>
              <span className="info-icon">üîî</span>
              <span>Recibe notificaciones importantes</span>
            </li>
            <li>
              <span className="info-icon">üìà</span>
              <span>Seguimiento de tu progreso</span>
            </li>
          </ul>
        </div>
      </div>

      <div className="registro-right">
        <div className="registro-card">
          <div className="registro-header">
            <h2>Crear Cuenta</h2>
            <p>√önete al sistema de onboarding</p>
          </div>

          {error && (
            <div className="alert alert-error">
              <span className="alert-icon">‚ö†Ô∏è</span>
              <span>{error}</span>
            </div>
          )}

          <form onSubmit={handleSubmit} className="registro-form">
            <div className="form-group">
              <label htmlFor="nombre_completo">Nombre Completo</label>
              <input
                type="text"
                id="nombre_completo"
                name="nombre_completo"
                className="form-input"
                placeholder="Juan P√©rez"
                value={formData.nombre_completo}
                onChange={handleChange}
                disabled={loading}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="correo">Correo Electr√≥nico</label>
              <input
                type="email"
                id="correo"
                name="correo"
                className="form-input"
                placeholder="tu.correo@bancobogota.com"
                value={formData.correo}
                onChange={handleChange}
                disabled={loading}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">Contrase√±a</label>
              <input
                type="password"
                id="password"
                name="password"
                className="form-input"
                placeholder="M√≠nimo 6 caracteres"
                value={formData.password}
                onChange={handleChange}
                disabled={loading}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="confirmarPassword">Confirmar Contrase√±a</label>
              <input
                type="password"
                id="confirmarPassword"
                name="confirmarPassword"
                className="form-input"
