import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/Login.css';

const Login = () => {
  const [correo, setCorreo] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    if (!correo || !password) {
      setError('Por favor completa todos los campos');
      setLoading(false);
      return;
    }

    const result = await login(correo, password);

    if (result.success) {
      navigate('/dashboard');
    } else {
      // Mensajes de error m√°s descriptivos
      if (result.error.includes('Credenciales inv√°lidas')) {
        setError('‚ùå Correo o contrase√±a incorrectos. Verifica tus datos.');
      } else if (result.error.includes('desactivado')) {
        setError('‚õî Tu cuenta ha sido desactivada. Contacta al administrador.');
      } else if (result.error.includes('no encontrado')) {
        setError('üë§ No existe una cuenta con este correo. ¬øQuieres registrarte?');
      } else {
        setError(result.error);
      }
    }

    setLoading(false);
  };

  return (
    <div className="login-container">
      <div className="login-left">
        <div className="login-brand">
          <div className="brand-logo">
            <img src="/Logo.png" alt="Banco de Bogot√°" className="logo-image" />
          </div>
          <h1>Banco de Bogot√°</h1>
          <p className="brand-subtitle">Sistema de Gesti√≥n de Onboarding</p>
        </div>

        <div className="login-info">
          <div className="info-card">
            <h3>Gesti√≥n Integral</h3>
            <p>Administra todo el proceso de onboarding de nuevos colaboradores en un solo lugar</p>
          </div>

          <div className="info-card">
            <h3>Calendario Inteligente</h3>
            <p>12 eventos t√©cnicos organizados durante todo el a√±o con Journey to Cloud y m√°s</p>
          </div>

          <div className="info-card">
                <h3>Alertas Autom√°ticas</h3>
            <p>Notificaciones por correo 7 d√≠as antes de cada evento t√©cnico programado</p>
          </div>

          <div className="info-card">
            <div className="info-icon">üìä</div>
            <h3>Seguimiento en Tiempo Real</h3>
            <p>Visualiza el progreso de cada colaborador y marca onboardings completados</p>
          </div>
        </div>

        <div className="login-decoration">
          <img src="/Imagen 2.jpg" alt="Onboarding" className="decoration-image" />
        </div>
      </div>

      <div className="login-right">
        <div className="login-card">
          <div className="login-header">
            <div className="header-icon">üîê</div>
            <h2>Iniciar Sesi√≥n</h2>
            <p>Accede a tu cuenta del sistema de onboarding</p>
          </div>

          {error && (
            <div className="alert alert-error">
              <span>{error}</span>
            </div>
          )}

          <form onSubmit={handleSubmit} className="login-form">
            <div className="form-group">
              <label htmlFor="correo">
                <span className="label-icon">üìß</span>
                Correo Electr√≥nico
              </label>
              <input
                type="email"
                id="correo"
                className="form-input"
                placeholder="tu.correo@bancobogota.com"
                value={correo}
                onChange={(e) => setCorreo(e.target.value)}
                disabled={loading}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">
                <span className="label-icon">üîí</span>
                Contrase√±a
              </label>
              <div className="password-input-wrapper">
                <input
                  type={showPassword ? 'text' : 'password'}
                  id="password"
                  className="form-input"
                  placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  disabled={loading}
                  required
                />
                <button
                  type="button"
                  className="password-toggle"
                  onClick={() => setShowPassword(!showPassword)}
                  tabIndex="-1"
                >
                  {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                </button>
              </div>
            </div>

            <button
              type="submit"
              className="btn btn-primary btn-block"
              disabled={loading}
            >
              {loading ? (
                <>
                  <span className="spinner-small"></span>
                  Iniciando sesi√≥n...
                </>
              ) : (
                <>
                  <span>Iniciar Sesi√≥n</span>
                  <span className="btn-arrow">‚Üí</span>
                </>
              )}
            </button>
          </form>

          <div className="login-footer">
            <p>
              ¬øNo tienes una cuenta?{' '}
              <Link to="/registro" className="link">
                Reg√≠strate aqu√≠
              </Link>
            </p>
          </div>

          <div className="login-demo-info">
            <p className="demo-title">üí° Usuario de Prueba</p>
            <p className="demo-text">
              <strong>Admin:</strong> admin@bancobogota.com / admin123
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
