import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/Navbar.css';

const Navbar = () => {
  const { user, logout, esAdminORRHH } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-brand">
          <Link to="/dashboard" className="brand-link">
            <span className="brand-icon">üè¶</span>
            <span className="brand-text">Banco de Bogot√°</span>
          </Link>
        </div>

        <div className="navbar-menu">
          <Link to="/dashboard" className="nav-link">
            <span className="nav-icon">üìä</span>
            Dashboard
          </Link>

          <Link to="/colaboradores" className="nav-link">
            <span className="nav-icon">üë•</span>
            Colaboradores
          </Link>

          <Link to="/calendario" className="nav-link">
            <span className="nav-icon">üìÖ</span>
            Calendario
          </Link>

          {esAdminORRHH() && (
            <Link to="/asignaciones" className="nav-link">
              <span className="nav-icon">üîó</span>
              Asignaciones
            </Link>
          )}
        </div>

        <div className="navbar-user">
          <div className="user-info">
            <div className="user-avatar">
              {user?.nombre_completo?.charAt(0).toUpperCase()}
            </div>
            <div className="user-details">
              <span className="user-name">{user?.nombre_completo}</span>
              <span className="user-role">
                {user?.rol === 'admin' ? 'üëë Admin' : 
                 user?.rol === 'rrhh' ? 'üëî RRHH' : 
                 'üë§ Colaborador'}
              </span>
            </div>
          </div>

          <button onClick={handleLogout} className="btn-logout">
            <span>üö™</span>
            Salir
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
