import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import '../styles/Login.css';

const Login = () => {
  const [correo, setCorreo] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Validaciones
    if (!correo || !password) {
      setError('Por favor completa todos los campos');
      setLoading(false);
      return;
    }

    // Intentar login
    const result = await login(correo, password);

    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.error);
    }

    setLoading(false);
  };

  return (
    <div className="login-container">
      <div className="login-left">
        <div className="login-brand">
          <div className="brand-logo">
            <span className="logo-icon">üè¶</span>
          </div>
          <h1>Banco de Bogot√°</h1>
          <p className="brand-subtitle">Sistema de Gesti√≥n de Onboarding</p>
        </div>

        <div className="login-features">
          <div className="feature">
            <span className="feature-icon">‚úì</span>
            <span>Gesti√≥n de colaboradores</span>
          </div>
          <div className="feature">
            <span className="feature-icon">‚úì</span>
            <span>Calendario de eventos</span>
          </div>
          <div className="feature">
            <span className="feature-icon">‚úì</span>
            <span>Alertas autom√°ticas</span>
          </div>
        </div>
      </div>

      <div className="login-right">
        <div className="login-card">
          <div className="login-header">
            <h2>Iniciar Sesi√≥n</h2>
            <p>Bienvenido de nuevo</p>
          </div>

          {error && (
            <div className="alert alert-error">
              <span className="alert-icon">‚ö†Ô∏è</span>
              <span>{error}</span>
            </div>
          )}

          <form onSubmit={handleSubmit} className="login-form">
            <div className="form-group">
              <label htmlFor="correo">Correo Electr√≥nico</label>
              <input
                type="email"
                id="correo"
                className="form-input"
                placeholder="tu.correo@bancobogota.com"
                value={correo}
                onChange={(e) => setCorreo(e.target.value)}
                disabled={loading}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">Contrase√±a</label>
              <input
                type="password"
                id="password"
                className="form-input"
                placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                disabled={loading}
                required
              />
            </div>

            <button
              type="submit"
              className="btn btn-primary btn-block"
              disabled={loading}
            >
              {loading ? (
                <>
                  <span className="spinner-small"></span>
                  Iniciando sesi√≥n...
                </>
              ) : (
                'Iniciar Sesi√≥n'
              )}
            </button>
          </form>

          <div className="login-footer">
            <p>
              ¬øNo tienes una cuenta?{' '}
              <Link to="/registro" className="link">
                Reg√≠strate aqu√≠
              </Link>
            </p>
          </div>

          <div className="login-demo-info">
            <p className="demo-title">üë§ Usuario de prueba (Admin):</p>
            <p className="demo-text">
              <strong>Correo:</strong> admin@bancobogota.com<br />
              <strong>Contrase√±a:</strong> admin123
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
