import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { colaboradoresAPI } from '../services/api';
import Navbar from '../components/Navbar';
import ModalColaborador from '../components/ModalColaborador';
import '../styles/Colaboradores.css';

const Colaboradores = () => {
  const { esAdminORRHH } = useAuth();
  const [colaboradores, setColaboradores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filtro, setFiltro] = useState('todos');
  const [busqueda, setBusqueda] = useState('');
  const [modalAbierto, setModalAbierto] = useState(false);
  const [colaboradorEditar, setColaboradorEditar] = useState(null);

  useEffect(() => {
    cargarColaboradores();
  }, []);

  const cargarColaboradores = async () => {
    try {
      setLoading(true);
      const response = await colaboradoresAPI.obtenerTodos();
      setColaboradores(response.data.colaboradores || []);
    } catch (error) {
      console.error('Error al cargar colaboradores:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleNuevoColaborador = () => {
    setColaboradorEditar(null);
    setModalAbierto(true);
  };

  const handleEditarColaborador = (colaborador) => {
    setColaboradorEditar(colaborador);
    setModalAbierto(true);
  };

  const handleEliminarColaborador = async (id) => {
    if (!window.confirm('¬øEst√°s seguro de eliminar este colaborador?')) {
      return;
    }

    try {
      await colaboradoresAPI.eliminar(id);
      cargarColaboradores();
    } catch (error) {
      console.error('Error al eliminar colaborador:', error);
      alert('Error al eliminar colaborador');
    }
  };

  const handleCompletarBienvenida = async (id) => {
    try {
      await colaboradoresAPI.completarBienvenida(id);
      cargarColaboradores();
    } catch (error) {
      console.error('Error al completar bienvenida:', error);
    }
  };

  const handleCompletarTecnico = async (id) => {
    try {
      await colaboradoresAPI.completarTecnico(id);
      cargarColaboradores();
    } catch (error) {
      console.error('Error al completar t√©cnico:', error);
    }
  };

  const colaboradoresFiltrados = colaboradores.filter((colab) => {
    // Filtro por estado
    if (filtro === 'completado') {
      if (!colab.onboarding_bienvenida || !colab.onboarding_tecnico) return false;
    } else if (filtro === 'pendiente') {
      if (colab.onboarding_bienvenida && colab.onboarding_tecnico) return false;
    }

    // Filtro por b√∫squeda
    if (busqueda) {
      const busquedaLower = busqueda.toLowerCase();
      return (
        colab.nombre_completo.toLowerCase().includes(busquedaLower) ||
        colab.correo.toLowerCase().includes(busquedaLower)
      );
    }

    return true;
  });

  const formatearFecha = (fecha) => {
    if (!fecha) return '-';
    return new Date(fecha).toLocaleDateString('es-CO');
  };

  if (loading) {
    return (
      <>
        <Navbar />
        <div className="page-container">
          <div className="loading-container">
            <div className="spinner"></div>
            <p>Cargando colaboradores...</p>
          </div>
        </div>
      </>
    );
  }

  return (
    <>
      <Navbar />
      <div className="page-container">
        <div className="page-header">
          <div>
            <h1>Gesti√≥n de Colaboradores</h1>
            <p className="page-subtitle">
              Administra los colaboradores y su progreso de onboarding
            </p>
          </div>
          {esAdminORRHH() && (
            <button onClick={handleNuevoColaborador} className="btn btn-primary">
              <span>‚ûï</span>
              Nuevo Colaborador
            </button>
          )}
        </div>

        {/* Filtros y b√∫squeda */}
        <div className="filters-bar">
          <div className="search-box">
            <span className="search-icon">üîç</span>
            <input
              type="text"
              placeholder="Buscar por nombre o correo..."
              value={busqueda}
              onChange={(e) => setBusqueda(e.target.value)}
              className="search-input"
            />
          </div>

          <div className="filter-buttons">
            <button
              className={`filter-btn ${filtro === 'todos' ? 'active' : ''}`}
              onClick={() => setFiltro('todos')}
            >
              Todos ({colaboradores.length})
            </button>
            <button
              className={`filter-btn ${filtro === 'completado' ? 'active' : ''}`}
              onClick={() => setFiltro('completado')}
            >
              Completado ({colaboradores.filter(c => c.onboarding_bienvenida && c.onboarding_tecnico).length})
            </button>
            <button
              className={`filter-btn ${filtro === 'pendiente' ? 'active' : ''}`}
              onClick={() => setFiltro('pendiente')}
            >
              Pendiente ({colaboradores.filter(c => !c.onboarding_bienvenida || !c.onboarding_tecnico).length})
            </button>
          </div>
        </div>

        {/* Tabla de colaboradores */}
        {colaboradoresFiltrados.length > 0 ? (
          <div className="table-container">
            <table className="data-table">
              <thead>
                <tr>
                  <th>Nombre</th>
                  <th>Correo</th>
                  <th>Fecha Ingreso</th>
                  <th>Onb. Bienvenida</th>
                  <th>Onb. T√©cnico</th>
                  {esAdminORRHH() && <th>Acciones</th>}
                </tr>
              </thead>
              <tbody>
                {colaboradoresFiltrados.map((colaborador) => (
                  <tr key={colaborador.id}>
                    <td className="td-name">{colaborador.nombre_completo}</td>
                    <td className="td-email">{colaborador.correo}</td>
                    <td>{formatearFecha(colaborador.fecha_ingreso)}</td>
                    <td>
                      {colaborador.onboarding_bienvenida ? (
                        <span className="badge badge-success">‚úì Completado</span>
                      ) : esAdminORRHH() ? (
                        <button
                          onClick={() => handleCompletarBienvenida(colaborador.id)}
                          className="btn-complete"
                        >
                          Marcar completado
                        </button>
                      ) : (
                        <span className="badge badge-warning">Pendiente</span>
                      )}
                    </td>
                    <td>
                      {colaborador.onboarding_tecnico ? (
                        <span className="badge badge-success">‚úì Completado</span>
                      ) : esAdminORRHH() ? (
                        <button
                          onClick={() => handleCompletarTecnico(colaborador.id)}
                          className="btn-complete"
                        >
                          Marcar completado
                        </button>
                      ) : (
                        <span className="badge badge-warning">Pendiente</span>
                      )}
                    </td>
                    {esAdminORRHH() && (
                      <td>
                        <div className="action-buttons">
                          <button
                            onClick={() => handleEditarColaborador(colaborador)}
                            className="btn-icon btn-edit"
                            title="Editar"
                          >
                            ‚úèÔ∏è
                          </button>
                          <button
                            onClick={() => handleEliminarColaborador(colaborador.id)}
                            className="btn-icon btn-delete"
                            title="Eliminar"
                          >
